					this->m_strr_Mbn_File_List[PATCH0_9X07_XML_FILE_I].cs_Path.Format(_T("%s\\%s"), cs_FW_Dir, cla_Finder.GetFileName());
					this->m_strr_Mbn_File_List[PATCH0_9X07_XML_FILE_I].b_Enable_DL = true;
